import React,{Component,Fragment} from 'react';
import './App.css';
import Form from './components/Form'

class  App extends Component {

  state = {
    userList: [],
    error: null,
    isLoaded: false,
  }

  componentDidMount() {
    fetch("http://demo9197058.mockable.io/users")
    .then(res => res.json())
    .then((result) => {
      this.setState({
        isLoaded: true,
        userList: result.slice(0,5)
      });
    })
  }
  getUsers = async (e) => {
    const searchTextVal= e.target.elements.searchText.value;
    e.preventDefault();
    console.log(searchTextVal);
    const api_call = await fetch("http://demo9197058.mockable.io/users");
    const data = await api_call.json();
    console.log(data)
    const user = data.filter(v => v.first_name.toLowerCase().indexOf(searchTextVal.toLowerCase()) > -1).slice(0, 5);
    console.log(user);

    this.setState({userList: user});


  }
  render() {
    if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
         return (
    <div className="App">
      <header className="App-header">
        <h1 className="App-title">Users</h1>
      </header>
      <Form getUsers={this.getUsers}/>
      <Fragment>

          <div className="container">


    <table className="table">
      <thead>
        <tr>
          <th>Id</th>
          <th>Name</th>
          <th>Email</th>
          <th>Age</th>
          <th>Company Name</th>
          <th>City</th>

        </tr>
      </thead>
      <tbody>
        {this.state.userList.map(({id,age,first_name,company_name,city,email}, index) => {
          return (
        <tr key={index}>

          <td>{id}</td>
          <td>{first_name}</td>
          <td>{email}</td>
          <td>{age}</td>
          <td>{company_name}</td>
          <td>{city}</td>

        </tr> 
          )
        })}


      </tbody>
    </table>

  </div>

          </Fragment>
    </div>
  );
      }
  }
}

export default App;
